    /**
     * Create a single book with its pages
     */
    private function createBook(array $bookData): void
    {
        $pages = $bookData['pages'] ?? [];
        $relatedTrainingTitleAr = $bookData['related_training_title_ar'] ?? null;

        // Remove pages and training title from book data
        unset($bookData['pages']);
        unset($bookData['related_training_title_ar']);

        // Generate folder name from title
        $folderName = $this->titleToSlug($bookData['title']);

        // Add cover and thumbnail paths
        $bookData['cover'] = "books/{$folderName}/cover.svg";
        $bookData['thumbnail'] = "books/{$folderName}/thumbnail.jpg";

        // Link to training if specified
        if ($relatedTrainingTitleAr) {
            $training = ExamTraining::where('title_ar', $relatedTrainingTitleAr)->first();
            if ($training) {
                $bookData['related_training_id'] = $training->id;
            }
        }

        // Create book
        $book = Book::create($bookData);

        $this->command->info("ðŸ“– Created book: {$book->title}");

        // Create folder structure
        $this->createBookFolders($folderName, count($pages));

        // Create pages
        foreach ($pages as $index => $pageText) {
            $pageNumber = $index + 1;

            Page::create([
                'book_id' => $book->id,
                'text' => $pageText,
                'image' => "books/{$folderName}/pages/page_{$pageNumber}/image.png",
                'mp3' => $book->has_sound ? "books/{$folderName}/pages/page_{$pageNumber}/audio.mp3" : null,
                'is_text_to_speech' => !$book->has_sound,
            ]);
        }

        $this->command->info("   âœ… Created " . count($pages) . " pages");
    }

    /**
     * Convert Arabic title to English slug for folder naming
     */
    private function titleToSlug(string $title): string
    {
        if (class_exists('Transliterator')) {
            $transliterator = \Transliterator::create('Any-Latin; Latin-ASCII');
            if ($transliterator) {
                $latinText = $transliterator->transliterate($title);
                return Str::slug($latinText);
            }
        }

        return Str::slug($title);
    }

    /**
     * Create folder structure for a book
     */
    private function createBookFolders(string $folderName, int $numberOfPages): void
    {
        $basePath = storage_path("app/public/books/{$folderName}");

        // Create main book folder
        File::makeDirectory($basePath, 0755, true, true);

        // Create pages folder and subfolders for each page
        for ($i = 1; $i <= $numberOfPages; $i++) {
            File::makeDirectory("{$basePath}/pages/page_{$i}", 0755, true, true);
        }
    }

    /**
     * Assign all books to a student
     */
    private function assignAllBooksToStudent(int $studentId): void
    {
        $books = Book::all();

        if ($books->isEmpty()) {
            $this->command->warn('âš ï¸  No books found. Skipping book assignment to student.');
            return;
        }

        $assignedCount = 0;
        foreach ($books as $book) {
            // Check if already assigned to avoid duplicates
            $existing = StudentBook::where('student_id', $studentId)
                ->where('book_id', $book->id)
                ->first();

            if (!$existing) {
                StudentBook::create([
                    'student_id' => $studentId,
                    'book_id' => $book->id,
                    'last_read_page_id' => null,
                    'is_favorite' => false,
                ]);
                $assignedCount++;
            }
        }

        $this->command->info("ðŸ“š Assigned {$assignedCount} books to student ID: {$studentId}");
    }
